import React, { memo, useState } from 'react';
import './App.css';

function App() {

  const [movement, setMovement] = useState(0)
  const [sliding, setSliding] = useState(false)
  const [lastTouch, setLastTouch] = useState(0)

  const handleTouchStart = e =>
    setLastTouch(e.nativeEvent.touches[0].clientX)

  const handleTouchEnd = () => setLastTouch(0)

  const handleTouchMove = e => {
    const delta = lastTouch - e.nativeEvent.touches[0].clientX;
    setLastTouch(e.nativeEvent.touches[0].clientX)

    handleMovement(delta)
  }

  const handleMovement = delta => {   
    setMovement(-50)
    return setSliding(true)
  };


  return (
    <div className="container">
      <div className="sliding">
        <div 
          className="slidingContent"
          onTouchStart={handleTouchStart}
          onTouchMove={handleMovement}
          onTouchEnd={handleTouchEnd}
          style={{
            transform: `translateX(${movement}px)`,
          }}
        >
          Your content here!
        </div>
        <div className="slidingOptions">
          Delete
        </div>
      </div>
    </div>
  );
}

export default memo(App);
