import React, { memo, useState } from 'react';
import './App.css';

function App() {
  const [width, setWith] = useState(75)
  const [openSliding, setOpenSliding] = useState(false)

  function open() {
    if (!openSliding) {
      setWith(55)
      return setOpenSliding(true)
    }
    setWith(100)
    return setOpenSliding(false)
  }
  return (
    <div className="sliding">
      <div 
        className="slidingContent"
        style={{ minWidth: `${width}vw`}}
        onClick={open}
      >
        Your content here!
      </div>
      <div className="slidingOptions">
        Delete
      </div>
    </div>
  );
}

export default memo(App);
